select.execute:

var snRequest,
  snData;

if ( method === "post" ) {
  snRequest = y.rest( url );
  snData = snRequest
    .timeout( 5000 )
    .accept( "text/plain, application/json" )
    .contentType( "application/x-www-form-urlencoded" )
    .post( data );
}        
else {
  snRequest = y.rest( url + "?" + data );
  snData = snRequest
    .timeout( 5000 )
    .accept( "text/plain, application/json" )
    .contentType( "application/x-www-form-urlencoded" )
    .get();
}

response.object = {
  "response": snData.response,
  "headers": snData.headers,
  "status": snData.status
};
